import React , { useEffect, useState }from 'react';
import { useNavigate } from 'react-router-dom';
import logo1 from '../../assets/img.PNG'; // Import du deuxième logo
import '../../styles.css'; // Assurez-vous d'importer les styles
import NavBar from  '../NavBar';
import {calculateNewSpeed} from '../calculateSpeed';

function Interface1({ accumulatedSpeed, setAccumulatedSpeed }) {
  const [clicked, setClicked] = useState(false);
  const navigate = useNavigate();

    // Fonction appelée lorsque l'utilisateur clique sur un bouton
    const handleClick = (value) => {
      const updatedSpeed = calculateNewSpeed(accumulatedSpeed, value);
      setAccumulatedSpeed(updatedSpeed); // Mise à jour de la vitesse accumulée
      setClicked(true); // Mise à jour de l'état "clicked" à vrai
    };
  
    // Utilisation de useEffect pour gérer la navigation après le clic
    useEffect(() => {
      if (clicked) {
        // Navigue uniquement si l'utilisateur a cliqué sur un bouton
        navigate('/Residentiel/Interface2');
      }
    }, [clicked, navigate]); // Exécution uniquement lorsque 'clicked' change
  
    return (
      <div className="main-content">
        <div className="header">
          {/* Par exemple, une barre de navigation ici */}
        </div>
        <div className="survey-content">
          <h1>Combien d'appareils dans votre maison se connectent à Internet ?</h1>
          <button className="survey-btn" onClick={() => handleClick(0.5)}>1-3</button>
          <button className="survey-btn" onClick={() => handleClick(1)}>4-7</button>
          <button className="survey-btn" onClick={() => handleClick(1.5)}>8-10</button>
          <button className="survey-btn" onClick={() => handleClick(2)}>10+</button>
        </div>
      </div>
    );
  }
  
  export default Interface1;
