import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo.png';
import '../styles.css';  // Assurez-vous d'importer les styles

const Int1 = () => {
  const navigate = useNavigate();
  const [employeeCount, setEmployeeCount] = useState(0);

 

  const goToInterface8 = () => {
    navigate('/Enterprise/Interface2');  // Navigue vers l'interface 2
  };

  const incrementCount = () => {
    setEmployeeCount(employeeCount + 1);
  };

  const decrementCount = () => {
    if (employeeCount > 0) {
      setEmployeeCount(employeeCount - 1);
    }
  };

  return (
    <div className="main-content">
      <div className="header">
        <NavBar />
      </div>
      <div className="survey-content">
        <h2 className="survey-title">Combien de vos employés peuvent être sur Internet en même temps pendant les heures d'utilisation maximale d'Internet?</h2>
        <p className="survey-description">Employés utilisant des périphériques tels que des ordinateurs de bureau, des ordinateurs portables, des tablettes / smartphones et des téléviseurs</p>
        
        <div className="employee-counter">
          <button className="counter-btn" onClick={decrementCount}>-</button>
          <span className="employee-count">{employeeCount}</span>
          <button className="counter-btn" onClick={incrementCount}>+</button>
        </div>
        
        <button className="next-btn" onClick={goToInterface8} disabled={employeeCount === 0}>Suivant</button>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <img src={logo} alt="Topnet Logo" className="logo" />
      <ProgressBar progress={60} />
      <SpeedDisplay speed={0} />
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div className="progress-bar-container">
      <div className="progress-bar" style={{ width: `${progress}%` }}></div>
    </div>
  );
}

function SpeedDisplay({ speed }) {
  return (
    <div className="speed-display-container">
      <div className="speed-text">
        Vitesse Minimum <br />
        Recommandée
      </div>
      <div className="speed-value">{speed} <span className="speed-unit">Mbps</span></div>
    </div>
  );
}

export default Int1;
