import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from '../NavBar';
import { EmployeeContext } from '../EmployeeContext';  // Importe le contexte

const Ife2 = () => {
    const navigate = useNavigate();
    const { employeeCount, setFixedEmployeeCount } = useContext(EmployeeContext);  // Récupère et fixe employeeCount du contexte
    const [localEmployeeCount, setLocalEmployeeCount] = useState(employeeCount);  // Initie employeeCount local
    const [deviceCount, setDeviceCount] = useState(0);  // Compteur total de devices

    const goToInterface3 = () => {
        const remainingEmployees = localEmployeeCount - deviceCount;
        if (remainingEmployees >= 0) {
            setFixedEmployeeCount(remainingEmployees);  // Fixe la valeur restante pour la prochaine interface
            navigate('/Entreprise/Ife3');
        }
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ife');  // Retourne à l'interface précédente
    };

    const incrementCount = () => {
        if (deviceCount < localEmployeeCount) {
            setDeviceCount(deviceCount + 1);  // Incrémente le nombre de devices
        }
    }

    const decrementCount = () => {
        if (deviceCount > 0) {
            setDeviceCount(deviceCount - 1);  // Décrémente le nombre de devices
        }
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>

            <div className="info-tooltip-section">
                <div className="go-back-text" onClick={handleBackClick}>
                    ❮ Retour
                </div>
            </div>

            <div className="survey-content">
                <h3>Pensez à votre moment le plus occupé pour l'utilisation d'Internet. Combien d'employés diffusent la vidéo en même temps?</h3>
                <p className="survey-description">Sur {localEmployeeCount}</p>  {/* Affiche le nombre local d'employés */}
                
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{deviceCount}</span>  {/* Affiche le nombre total de devices */}
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                
                <button className="next-btn" onClick={goToInterface3} disabled={deviceCount === 0}>Suivant</button>
            </div>

            {/* Section Qualité vidéo */}
            <div className="video-quality-section">
                <p className="quality-description">
                    Facultatif: sélectionnez la qualité de la vidéo que vous diffuserez en continu. La plupart des services comme YouTube et Pluralsight diffusent en HD.
                </p>
                <div className="quality-options">
                    <VideoQualityButton
                        label="Vidéo floue"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                    <VideoQualityButton
                        label="Basse définition"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                    <VideoQualityButton
                        label="Définition standard"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                    <VideoQualityButton
                        label="HD (1080p)"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                    <VideoQualityButton
                        label="Ultra HD (4k)"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                    <VideoQualityButton
                        label="Je ne sais pas"
                        deviceCount={deviceCount}
                        setDeviceCount={setDeviceCount}
                        localEmployeeCount={localEmployeeCount}
                    />
                </div>
            </div>
        </div>
    );
};

const VideoQualityButton = ({ label, deviceCount, setDeviceCount, localEmployeeCount }) => {
    const [count, setCount] = useState(0);  // Compteur local pour chaque bouton

    const incrementDeviceCount = () => {
        if (deviceCount < localEmployeeCount) {
            setCount(count + 1);
            setDeviceCount(deviceCount + 1);
        }
    };

    const decrementDeviceCount = () => {
        if (count > 0) {
            setCount(count - 1);
            setDeviceCount(deviceCount - 1);
        }
    };

    return (
        <div className="quality-counter">
            <div className="device-count-display">{count > 0 ? `${count} devices` : ''}</div> {/* Affiche le compteur au-dessus du bouton */}
            <div className="button-group">
                <button className="quality-btn" onClick={decrementDeviceCount}>-</button>
                <span>{label}</span>
                <button className="quality-btn" onClick={incrementDeviceCount}>+</button>
            </div>
        </div>
    );
};

export default Ife2;
