import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../assets/logo.png';
import emp from '../../assets/emp.PNG';
import '../../styles.css';  // Assurez-vous d'importer les styles

const Ife2 = () => {
    const navigate = useNavigate();
    const [employeeCount, setEmployeeCount] = useState(0);

    const goToInterface2 = () => {
        navigate('/Entreprise/Ife2');  // Navigue vers l'interface 2
    };
    
    const incrementCount = () => {
        setEmployeeCount(employeeCount + 1);
    };

    const decrementCount = () => {
        if (employeeCount > 0) {
          setEmployeeCount(employeeCount - 1);
        }
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>
            <div className="go-back-text" onClick={goBack}>
                Retour
            </div>
            {/* Intégration du composant InfoTooltip sous la NavBar */}
            <div className="info-tooltip-section">
                <InfoTooltip />
            </div>
            <div className="survey-content">
                <h2 className="survey-title">Pensez à votre moment le plus occupé pour l'utilisation d'Internet. Combien d'employés diffusent la vidéo en même temps? (YouTube, Pluralsight, Lynda.com, etc.)</h2>
                <p className="survey-description">Sur ?</p>
                
                <div className="employees-container">
                    <img src={emp} alt="employee icon" />
                </div>
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{employeeCount}</span>
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                <button className="next-btn" onClick={goToInterface2} disabled={employeeCount === 0}>Suivant</button>
            </div>

            {/* Deuxième partie */}
            <div className="video-quality-section">
                <p className="quality-description">
                    Facultatif: sélectionnez la qualité de la vidéo que vous diffuserez en continu. La plupart des services comme YouTube et Pluralsight diffusent en HD.
                </p>
                <div className="quality-options">
                    <button className="quality-btn">Vidéo floue</button>
                    <button className="quality-btn">Basse définition</button>
                    <button className="quality-btn">Définition standard</button>
                    <button className="quality-btn">HD (1080p)</button>
                    <button className="quality-btn">Ultra HD (4k)</button>
                    <button className="quality-btn">Je ne sais pas</button>
                </div>
            </div>
        </div>
    );
}

function NavBar() {
    return (
        <div className="navbar">
            <img src={logo} alt="Topnet Logo" className="logo" />
            <ProgressBar progress={60} />
            <SpeedDisplay speed={0} />
        </div>
    );
}

function ProgressBar({ progress }) {
    return (
        <div className="progress-bar-container">
            <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        </div>
    );
}

function SpeedDisplay({ speed }) {
    return (
        <div className="speed-display-container">
            <div className="speed-text">
                Vitesse Minimum <br />
                Recommandée
            </div>
            <div className="speed-value">{speed} <span className="speed-unit">Mbps</span></div>
        </div>
    );
}

function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                        Nous prenons en compte le nombre d'utilisateurs de votre entreprise qui 
                        utilisent Internet sur différents appareils à la fois. C'est un facteur primordial 
                        pour déterminer les vitesses Internet dont vous aurez besoin au moment de l'utilisation 
                        de pointe.
                    </p>
                </div>
            )}
        </div>
    );
}

export default Ife2;
