import React, { useState } from 'react';
import MainContent from './components/MainContent';  // Interface initiale
import Survey from './components/survey';
function App() {
    const [view, setView] = useState('home');  // 'home' pour la première interface, 'survey' pour la deuxième

    const handleStartClick = () => {
        setView('survey');
    };

    return (
        <div>
            {view === 'home' && <MainContent onStartClick={handleStartClick} />}
            {view === 'survey' && <Survey />}
        </div>
    );
}

export default App;
