import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo.png';
import '../styles.css';  // Assurez-vous d'importer les styles

function Interface2() {
  const navigate = useNavigate();

  const handleBackClick = () => {
    navigate('/Interface1'); 
  };

  const goToInterface3 = () => {
    navigate('/Interface3');  // Navigue vers l'interface 3
  };

  return (
    <div className="main-content">  {/* Utilise les mêmes classes que Interface1 */}
      <div className="header">
        <NavBar />
      </div>
      <div className="survey-content">
        <div className="back-link" onClick={handleBackClick} >&lt;Back</div> {/* Lien pour revenir en arrière */}
        <h2>Combien de personnes utilisent Internet chez vous?</h2>
        <div className="survey-options">
          <button className="survey-btn" onClick={goToInterface3}>1-2</button>
          <button className="survey-btn" onClick={goToInterface3}>3-4</button>
          <button className="survey-btn" onClick={goToInterface3}>4+</button>
        </div>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <img src={logo} alt="Topnet Logo" className="logo1" />
      <ProgressBar progress={10} />
      {/* Ajout du composant SpeedDisplay ici */}
      <SpeedDisplay speed={0} />
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div className="progress-bar-container">
      <div className="progress-bar" style={{ width: `${progress}%` }}></div>
      <span className="progress-text">{`${progress}%`}</span>
    </div>
  );
}

  function SpeedDisplay({ speed }) {
    return (
      <div className="speed-display-container">
        <div className="speed-text">
          Vitesse Minimum <br />
          Recommandée
        </div>
        <div className="speed-value">{speed} <span className="speed-unit">Mbps</span></div>
      </div>
    );
    
}

export default Interface2;
