import React, { createContext, useState } from 'react';

// Création du contexte
export const EmployeeContext = createContext();

export const EmployeeProvider = ({ children }) => {
  const [employeeCount, setFixedEmployeeCount] = useState(0);  // Crée l'état pour stocker le nombre d'employés

  return (
    <EmployeeContext.Provider value={{ employeeCount, setFixedEmployeeCount }}>
      {children}
    </EmployeeContext.Provider>
  );
};
