import React, { useState } from 'react';
import logo from '../assets/logo.png';
import Interface4 from './Interface4';

function Interface3({ onNavigate }) {
  const [showNextInterface, setShowNextInterface] = useState(false);

  const onStartClick = () => {
    setShowNextInterface(true);
  };

  if (showNextInterface) {
    // Remplacez 'NextInterface' par la prochaine interface si elle existe.
    return <Interface4/>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <NavBar /*progress={66} debit={15} *//> {/* Exemples de valeurs pour la barre de progression et le débit */}

      <div style={{ textAlign: 'center' }}>
        <h2>Quel type de contenu consommez-vous principalement en ligne?</h2>
        <button className="survey-btn" onClick={onStartClick}>Streaming vidéo</button>
        <button className="survey-btn" onClick={onStartClick}>Jeux en ligne</button>
        <button className="survey-btn" onClick={onStartClick}>Réseaux sociaux</button>
        <button className="survey-btn" onClick={onStartClick}>Navigation web</button>
      </div>
    </div>
  );
}

function NavBar(/*{ progress, debit }*/) {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
      <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
      <ProgressBar /*progress={progress} *//>
      <div style={{ fontSize: '20px' }}> Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div style={{ width: '50%', backgroundColor: '#444', borderRadius: '5px' }}>
      <div style={{backgroundColor: '#ff4081', height: '10px', borderRadius: '5px' }}></div>
    </div>
  );
}

export default Interface3;
