import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from  '../NavBar';




const Ife5 = () => {
    const navigate = useNavigate();
    const { employeeCount } = useContext(EmployeeContext);  // Récupère la valeur globale de employeeCount
    const [localEmployeeCount, setLocalEmployeeCount] = useState(0);

    const goToInterface5 = () => {
        navigate('/Entreprise/Ife5');  // Navigue vers l'interface 5
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ifee3'); 
      };
    
    const incrementCount = () => {
        if (localEmployeeCount < employeeCount) {
            setLocalEmployeeCount(localEmployeeCount + 1);  // Modifie uniquement la valeur locale
        }
    };

    const decrementCount = () => {
        if (employeeCount > 0) {
            setLocalEmployeeCount(employeeCount - 1);
        }
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>
    
            {/* Intégration du composant InfoTooltip sous la NavBar */}
            <div className="info-tooltip-section">
            <div className="go-back-text" onClick={handleBackClick}>
            ❮ Retour
            </div>
                <InfoTooltip />
            </div>
            <div className="survey-content">
                <h3>Combien d’employés diffuseraient de l’audio, écouteraient de la musique ou utiliseraient la VOIP ? (appels Internet, Spotify, Pandora, etc.)
                </h3>
                <p className="survey-description">Sur ?</p>
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{employeeCount}</span>
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                <button className="next-btn" onClick={goToInterface6} >Suivant</button>
            </div>

            {/* Deuxième partie */}
            <div className="video-quality-section">
                <p className="quality-description">
                Facultatif : sélectionnez la qualité audio que vous diffuserez. La plupart des services comme Spotify et Pandora diffusent en haute qualité (192 kbps).
                </p>
                <div className="quality-options">
                    <button className="quality-btn">Mauvaise qualité</button>
                    <button className="quality-btn">Haute qualité</button>
                    <button className="quality-btn">Très haute qualité</button>
                    <button className="quality-btn">Je ne sais pas</button>
                </div>
            </div>
        </div>
    );
}


function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                    Bien que les activités audio ne consomment pas beaucoup de bande passante individuellement, vous devez veiller à ce que vos vitesses Internet répondent à vos besoins VOIP pour tous les employés susceptibles d'utiliser le téléphone en même temps.
                    </p>
                </div>
            )}
        </div>
    );
}

export default Ife5
