import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo.png';  // Assurez-vous que le chemin vers l'image est correct
import logo1 from '../assets/img.PNG';  // Assurez-vous que le chemin vers l'image est correct
import '../styles.css';  

function Interface1() {
  const navigate = useNavigate();

  const goToInterface2 = (value) => {
    navigate('/Interface2');  // Navigue vers l'interface 2
  };

  return (
    <div>
      <div className="header">
        <NavBar />
      </div>
      <div className="survey-content">
        <h1>Combien d'appareils dans votre maison se connectent à Internet?</h1>
        <p className="survey-description">(Ordinateurs, smartphones, tablettes, consoles de jeu, téléviseurs intelligents, appareils domestiques intelligents, etc.)</p>
        <div className="icons-container">
          <img src={logo1} alt="Connected Devices Icons" />
        </div>
        <div className="survey-options">
          <button className="survey-btn" onClick={() => goToInterface2(5)}>1-3</button>
          <button className="survey-btn" onClick={() => goToInterface2(10)}>4-7</button>
          <button className="survey-btn" onClick={() => goToInterface2(15)}>8-10</button>
          <button className="survey-btn" onClick={() => goToInterface2(20)}>10+</button>
        </div>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <img src={logo} alt="Topnet Logo" className="logo" />
      <div className="progress-container">
        <ProgressBar progress={10} />
      </div>
      <div className="speed-display">0 Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div className="progress-bar">
      <div className="progress" style={{ width: `${progress}%` }}></div>
    </div>
  );
}

export default Interface1;
