import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from  '../NavBar';



const Ife2 = () => {
    const navigate = useNavigate();
    const [employeeCount, setEmployeeCount] = useState(0);

    const goToInterface3 = () => {
        navigate('/Entreprise/Ifee3');  // Navigue vers l'interface 3
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ife'); 
      };
    
    const incrementCount = () => {
        setEmployeeCount(employeeCount + 1);
    };

    const decrementCount = () => {
        if (employeeCount > 0) {
          setEmployeeCount(employeeCount - 1);
        }
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>
    
            {/* Intégration du composant InfoTooltip sous la NavBar */}
            <div className="info-tooltip-section">
            <div className="go-back-text" onClick={handleBackClick}>
            ❮ Retour
            </div>
                <InfoTooltip />
            </div>
            <div className="survey-content">
                <h3>Pensez à votre moment le plus occupé pour l'utilisation d'Internet. Combien d'employés diffusent la vidéo en même temps? (YouTube, Pluralsight, Lynda.com, etc.)</h3>
                <p className="survey-description">Sur ?</p>
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{employeeCount}</span>
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                <button className="next-btn" onClick={goToInterface3} >Suivant</button>
            </div>

            {/* Deuxième partie */}
            <div className="video-quality-section">
                <p className="quality-description">
                    Facultatif: sélectionnez la qualité de la vidéo que vous diffuserez en continu. La plupart des services comme YouTube et Pluralsight diffusent en HD.
                </p>
                <div className="quality-options">
                    <button className="quality-btn">Vidéo floue</button>
                    <button className="quality-btn">Basse définition</button>
                    <button className="quality-btn">Définition standard</button>
                    <button className="quality-btn">HD (1080p)</button>
                    <button className="quality-btn">Ultra HD (4k)</button>
                    <button className="quality-btn">Je ne sais pas</button>
                </div>
            </div>
        </div>
    );
}



function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                    Certaines activités consomment plus de bande passante que d'autres. Le streaming vidéo en HD en fait certainement partie. Notre outil de vitesse tient compte de la façon dont vous utilisez vos vitesses Internet et permet une bande passante supplémentaire pour s'adapter aux activités que vous aimez le plus.
                    </p>
                </div>
            )}
        </div>
    );
}

export default Ife2;
