import React, { useState } from 'react';
import logo from '../assets/logo.png';
import Interface3 from './Interface3'; // Importez la troisième interface

function Interface2({ onNavigate }) {
  const [showInterface3, setShowInterface3] = useState(false); // État pour montrer la troisième interface

  const onStartClick = () => {
    setShowInterface3(true);
  };

  if (showInterface3) {
    return <Interface3 />; // Afficher Interface3 lorsque l'utilisateur clique sur un bouton
  }

  return (
    <div style={{ padding: '20px' }}>
      <NavBar progress={33} debit={10} /> {/* Exemples de valeurs pour la barre de progression et le débit */}

      <div style={{ textAlign: 'center' }}>
        <h2>Combien de personnes utilisent Internet chez vous?</h2>
        <button className="survey-btn" onClick={onStartClick}>1-2</button>
        <button className="survey-btn" onClick={onStartClick}>3-4</button>
        <button className="survey-btn" onClick={onStartClick}>4+</button>
      </div>
    </div>
  );
}

function NavBar({ progress, debit }) {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
      <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
      <ProgressBar progress={progress} />
      <div style={{ fontSize: '20px' }}>{debit} Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div style={{ width: '50%', backgroundColor: '#444', borderRadius: '5px' }}>
      <div style={{ width: `${progress}%`, backgroundColor: '#ff4081', height: '10px', borderRadius: '5px' }}></div>
    </div>
  );
}

export default Interface2;
