import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo.png';



function Interface2() {
  const [showInterface3, setShowInterface3] = useState(false); // État pour montrer la troisième interface
  const navigate = useNavigate();

  const handleClick = () => {
    navigate('/Interface1'); // Navigate back to Interface1
  };

  if (showInterface3) {
    // Remplacez par l'affichage de l'interface 3
  }

  return (
    <div style={{ padding: '20px' }}>
      <NavBar />
      <div style={{ marginBottom: '20px', cursor: 'pointer', color: 'black' }} onClick={handleClick}>
        &lt;Back
      </div>
      <div style={{ textAlign: 'center' }}>
        <h2>Combien de personnes utilisent Internet chez vous?</h2>
        <button className="survey-btn" onClick={() => setShowInterface3(true)}>1-2</button>
        <button className="survey-btn" onClick={() => setShowInterface3(true)}>3-4</button>
        <button className="survey-btn" onClick={() => setShowInterface3(true)}>4+</button>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
      <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
      <div style={{ fontSize: '20px' }}>Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div style={{ width: '50%', backgroundColor: '#444', borderRadius: '5px' }}>
      <div style={{  backgroundColor: '#ff4081', height: '10px', borderRadius: '5px' }}></div>
    </div>
  );
}

export default Interface2;
