import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from '../NavBar';

const Ifee3 = ({ initialEmployeeCount, previousLocalEmployeeCount }) => {
    const navigate = useNavigate();

    // Initialisation des valeurs selon les deux cas
    const initialLocalCount = initialEmployeeCount > previousLocalEmployeeCount 
        ? initialEmployeeCount - previousLocalEmployeeCount 
        : 0;

    const [manualMode, setManualMode] = useState(initialEmployeeCount === previousLocalEmployeeCount);
    const [localEmployeeCount, setLocalEmployeeCount] = useState(initialLocalCount);
    const [displayCount, setDisplayCount] = useState(initialLocalCount); // Affichage de la bonne valeur

    // Limite maximale pour le compteur local
    const maxCount = initialEmployeeCount > previousLocalEmployeeCount 
        ? initialEmployeeCount - previousLocalEmployeeCount 
        : initialEmployeeCount;

    useEffect(() => {
        if (initialEmployeeCount > previousLocalEmployeeCount) {
            const difference = initialEmployeeCount - previousLocalEmployeeCount;
            setDisplayCount(difference);  // Affiche la différence
            setLocalEmployeeCount(difference);  // Initialise le compteur local avec la différence
        } else if (initialEmployeeCount === previousLocalEmployeeCount) {
            setManualMode(true);
            setDisplayCount(localEmployeeCount);  // Affiche la nouvelle valeur manuelle
        }
    }, [initialEmployeeCount, previousLocalEmployeeCount]);

    const incrementCount = () => {
        if (manualMode && localEmployeeCount < maxCount) {
            setLocalEmployeeCount(localEmployeeCount + 1);
            setDisplayCount(localEmployeeCount + 1);  // Met à jour l'affichage
        }
    };

    const decrementCount = () => {
        if (manualMode && localEmployeeCount > 0) {
            setLocalEmployeeCount(localEmployeeCount - 1);
            setDisplayCount(localEmployeeCount - 1);  // Met à jour l'affichage
        }
    };

    const goToInterface4 = () => {
        navigate('/Entreprise/Ife4');
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ife2');
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>

            <div className="info-tooltip-section">
                <div className="go-back-text" onClick={handleBackClick}>
                    ❮ Retour
                </div>
                <InfoTooltip />
            </div>

            <div className="survey-content">
                <h3>Combien d'employés naviguent sur Internet ou vérifient leurs courriels en même temps?</h3>
                {/* Affiche la différence ou la nouvelle valeur manuelle */}
                <p className="survey-description">Sur {displayCount}</p>
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{localEmployeeCount}</span>
                    {manualMode && <button className="counter-btn" onClick={incrementCount}>+</button>}
                </div>
                <button className="next-btn" onClick={goToInterface4}>Suivant</button>
            </div>
        </div>
    );
};

function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                        Bien que des activités telles que la messagerie électronique et la navigation sur Internet n'utilisent généralement pas une grande quantité de bande passante, les canaux de médias sociaux riches en vidéos ou le téléchargement fréquent d'images HD volumineuses peuvent affecter les performances du réseau.
                    </p>
                </div>
            )}
        </div>
    );
}

export default Ifee3;
