import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from  '../NavBar';
import { EmployeeContext } from '../EmployeeContext';  // Importe le contexte




const Ife4 = () => {
    const navigate = useNavigate();
    const [employeeCount, setEmployeeCount] = useState(0);

    const goToInterface5 = () => {
        navigate('/Entreprise/Ife5');  // Navigue vers l'interface 5
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ifee3'); 
      };
    
    const incrementCount = () => {
        setEmployeeCount(employeeCount + 1);
    };

    const decrementCount = () => {
        if (employeeCount > 0) {
          setEmployeeCount(employeeCount - 1);
        }
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>
    
            {/* Int√©gration du composant InfoTooltip sous la NavBar */}
            <div className="info-tooltip-section">
            <div className="go-back-text" onClick={handleBackClick}>
            ‚ùÆ Retour
            </div>
                <InfoTooltip />
            </div>
            <div className="survey-content">
                <h3>Combien d'employ√©s seraient en appels vid√©o?</h3>
                <p className="survey-description">Vid√©oconf√©rence, Skype, etc.</p>
                <p className="survey-description">Sur ?</p>
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{employeeCount}</span>
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                <button className="next-btn" onClick={goToInterface5} >Suivant</button>
            </div>

            {/* Deuxi√®me partie */}
            <div className="video-quality-section">
                <p className="quality-description">
                Facultatif: S√©lectionnez la qualit√© vid√©o et le nombre de personnes sur les appels vid√©o?
                </p>
                <div className="quality-options">
                    <button className="quality-btn">Petit groupe (3-5)</button>
                    <button className="quality-btn">Grand groupe (7+)</button>
                    <button className="quality-btn">Groupe bas</button>
                    <button className="quality-btn">Groupe standard</button>
                    <button className="quality-btn">HD (1080p)</button>
                    <button className="quality-btn">Je ne sais pas</button>

                </div>
            </div>
        </div>
    );
}



function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                    Bien que les appels vid√©o ne n√©cessitent pas de vitesses √©lev√©es, vous devrez pr√©voir un peu de bande passante suppl√©mentaire pour les appels vid√©o HD ou les conf√©rences t√©l√©phoniques avec de grands groupes.
                    </p>
                </div>
            )}
        </div>
    );
}



export default Ife4
