import React, { useState } from 'react';
import logo from '../assets/img.PNG';
import Survey2 from ''

function Survey() {
    const [showSurvey2, setShowSurvey2] = useState(false); // État pour gérer la navigation

    const handleOptionClick = () => {
        setShowSurvey2(true); // Afficher SurveyPage2 après le clic
    };

    if (showSurvey2) {
        return <survey2 />;
    }

    return (
        <div className="survey-content">
            <NavBar />
            <h1>Combien d'appareils dans votre maison se connectent à Internet?</h1>
            <p>(Ordinateurs, smartphones, tablettes, consoles de jeu, téléviseurs intelligents, appareils domestiques intelligents, etc.)</p>
            <div><img src={logo} alt="Topnet Logo" /></div>
            <div className="survey-options">
                <button className="survey-btn" onClick={handleOptionClick}>1-3</button>
                <button className="survey-btn" onClick={handleOptionClick}>4-7</button>
                <button className="survey-btn" onClick={handleOptionClick}>8-10</button>
                <button className="survey-btn" onClick={handleOptionClick}>10+</button>
            </div>
        </div>
    );
}

function NavBar() {
    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
            <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
            <div style={{ fontSize: '20px' }}>Survey Page 1</div>
        </div>
    );
}

export default Survey;
