import React, { useState } from 'react';
import logo from '../assets/logo.png';


function Interface2({ onNavigate }) {
  //const [progress, setProgress] = useState(22); // Pourcentage de progression
  const [debit, setDebit] = useState(5); // Valeur initiale du débit

  return (
    <div style={{ padding: '20px' }}>
      <NavBar progress={progress} debit={debit} />

      <div style={{ textAlign: 'center' }}>
        <h2>Combien de personnes utilisent Internet chez vous?</h2>
        <button className="survey-btn" onClick={() => handleChoice(1)} >1-2</button>
        <button className="survey-btn" onClick={() => handleChoice(3)} >3-4</button>
        <button className="survey-btn" onClick={() => handleChoice(5)} >4+</button>
      </div>
    </div>
  );

  function handleChoice(people) {
    const newDebit = debit + people * 2; // Exemple simple de calcul
    setDebit(newDebit);
    setProgress(progress + 20); // Mettre à jour la progression
    onNavigate('NextPage'); // Naviguer vers la page suivante (à définir)
  }
}

function NavBar({ progress, debit }) {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
      <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
      <ProgressBar progress="{progress}%" />
      <div style={{ fontSize: '20px' }}>{debit} Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div style={{ width: '50%', backgroundColor: '#444', borderRadius: '5px' }}>
      <div style={{ width: `${progress}%`, backgroundColor: '#ff4081', height: '10px', borderRadius: '5px' }}></div>
    </div>
  );
}



export default Interface2;
