import React, { useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from '../NavBar';
import { EmployeeContext } from '../EmployeeContext';  // Importe le contexte

const Ife4 = ({ previousLocalEmployeeCount }) => {
    const navigate = useNavigate();
    const { employeeCount } = useContext(EmployeeContext);  // Utilise le contexte

    // Calcul de la valeur initiale de localEmployeeCount
    const initialLocalCount = employeeCount > previousLocalEmployeeCount 
        ? employeeCount - previousLocalEmployeeCount 
        : 0;

    // Limite maximale pour le compteur local
    const maxCount = employeeCount > previousLocalEmployeeCount 
        ? employeeCount - previousLocalEmployeeCount 
        : employeeCount;

    const [localEmployeeCount, setLocalEmployeeCount] = useState(initialLocalCount);
    const [qualityCounts, setQualityCounts] = useState({
        "Basse définition": 0,
        "Définition standard": 0,
        "HD (1080p)": 0,
        "Je ne sais pas": 0,
    });

    // Mise à jour de localEmployeeCount si employeeCount et previousLocalEmployeeCount sont égaux
    useEffect(() => {
        if (employeeCount === previousLocalEmployeeCount) {
            setLocalEmployeeCount(0);  // Réinitialise à 0 pour une mise à jour manuelle
        }
    }, [employeeCount, previousLocalEmployeeCount]);
    

    const goToInterface5 = () => {
        const totalSelectedDevices = Object.values(qualityCounts).reduce((sum, count) => sum + count, 0);
        if (totalSelectedDevices === localEmployeeCount) {
            navigate('/Entreprise/Ife5');  // Navigue vers Interface 5 seulement si la condition est remplie
        } else {
            alert('Le nombre total de participants doit correspondre au nombre d’employés.');
        }
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ifee3');  // Retourne à l'interface précédente
    };

    const incrementCount = () => {
        if (localEmployeeCount < employeeCount + maxCount) {
            setLocalEmployeeCount(localEmployeeCount + 1);
        } else {
            alert(`Le nombre ne peut pas dépasser ${employeeCount + maxCount}.`);
        }
    };

    const decrementCount = () => {
        if (localEmployeeCount > 0) {
            setLocalEmployeeCount(localEmployeeCount - 1);
        }
    };

    const handleQualityIncrement = (quality) => {
        const totalSelectedDevices = Object.values(qualityCounts).reduce((sum, count) => sum + count, 0);
        if (totalSelectedDevices < localEmployeeCount) {
            setQualityCounts(prevCounts => ({
                ...prevCounts,
                [quality]: prevCounts[quality] + 1,
            }));
        } else {
            alert('Le nombre total d’appareils a atteint la limite.');
        }
    };

    const handleQualityDecrement = (quality) => {
        if (qualityCounts[quality] > 0) {
            setQualityCounts(prevCounts => ({
                ...prevCounts,
                [quality]: prevCounts[quality] - 1,
            }));
        }
    };

   

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>

            <div className="info-tooltip-section">
                <div className="go-back-text" onClick={handleBackClick}>
                    ❮ Retour
                </div>
                <InfoTooltip />
            </div>

            <div className="survey-content">
                <h3>Combien d'employés seraient en appels vidéo ?</h3>
                <p className="survey-description">Vidéoconférence, Skype, etc.</p>
                <p className="survey-description">Sur {localEmployeeCount}</p>  {/* Affiche la valeur globale */}

                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{localEmployeeCount}</span>  {/* Affiche la valeur locale */}
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>

                <button className="next-btn" onClick={goToInterface5}>Suivant</button>
            </div>

            {/* Deuxième partie : Section qualité vidéo */}
            <div className="video-quality-section">
                <p className="quality-description">
                    Facultatif: Sélectionnez la qualité vidéo et le nombre de personnes sur les appels vidéo ?
                </p>
                <div className="quality-options">
                    <button className="quality-btn" onClick={() => ("Petit groupe (3-5)")}>
                        Petit groupe (3-5)
                    </button>
                    <button className="quality-btn" onClick={() => ("Grand groupe (7+)")}>
                        Grand groupe (7+)
                    </button>
                    {Object.keys(qualityCounts).map((quality) => (
                        <div key={quality} className="quality-option">
                            <button className="quality-btn" onClick={() => handleQualityClick(quality)}>
                                {quality}
                            </button>

                            {/* Affiche le compteur seulement si ce n'est pas "Petit groupe" ou "Grand groupe" */}
                            {qualityCounts[quality] > 0 && (
                                <div className="quality-counter">
                                    <button 
                                        className="counter-butn"
                                        onClick={() => handleQualityDecrement(quality)}
                                    >
                                        -
                                    </button>
                                    <span className="quality-count">
                                        {qualityCounts[quality]} devices
                                    </span>
                                    <button 
                                        className="counter-butn"
                                        onClick={() => handleQualityIncrement(quality)}
                                    >
                                        +
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

function InfoTooltip() {
    const [isVisible, setIsVisible] = useState(false);

    const toggleTooltip = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className="tooltip-container">
            <div className="tooltip-icon" onClick={toggleTooltip}>?</div>
            {isVisible && (
                <div className="tooltip-box">
                    <p>
                        Les appels vidéo ne nécessitent pas de vitesses élevées, mais vous aurez besoin de plus de bande passante pour des appels vidéo HD ou des conférences avec de grands groupes.
                    </p>
                </div>
            )}
        </div>
    );
}

export default Ife4;
