import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/img.PNG';
import logo1 from '../assets/img.PNG';
import '.../styles.css';

function Interface1() {
  const navigate = useNavigate();

  const goToInterface2 = (value) => {
    navigate('/Interface2');  // Navigue vers l'interface 2
  };

  return (
    <div className="main-content">
      <div className="header">
        <NavBar />
      </div>
      <div className="survey-content">
        <h1>Combien d'appareils dans votre maison se connectent à Internet?</h1>
        <p className="survey-description">
          (Ordinateurs, smartphones, tablettes, consoles de jeu, téléviseurs intelligents, appareils domestiques intelligents, etc.)
        </p>
        <div className="icons-container">
          <img src={logo1} alt="Connected Devices Icons" className="survey-img" />
        </div>
        <div className="survey-options">
          <button className="survey-btn" onClick={() => goToInterface2(5)}>1-3</button>
          <button className="survey-btn" onClick={() => goToInterface2(10)}>4-7</button>
          <button className="survey-btn" onClick={() => goToInterface2(15)}>8-10</button>
          <button className="survey-btn" onClick={() => goToInterface2(20)}>10+</button>
        </div>
      </div>
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <img src={logo} alt="Topnet Logo" className="logo1" />
      <ProgressBar progress={10} />
      {/* Ajout du composant SpeedDisplay ici */}
      <SpeedDisplay speed={0} />
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div className="progress-bar-container">
      <div className="progress-bar" style={{ width: `${progress}%` }}></div>
      <span className="progress-text">{`${progress}%`}</span>
    </div>
  );
}

/* Composant SpeedDisplay */
function SpeedDisplay({ speed }) {
  return (
    <div className="speed-display-container">
      <div className="speed-text">
        Vitesse Minimum <br />
        Recommandée
      </div>
      <div className="speed-value">{speed} <span className="speed-unit">Mbps</span></div>
    </div>
  );
}

export default Interface1;
