import React, { useState } from 'react';
import logo from '../assets/logo.png';
import Interface5 from './Interface5';

function Interface4({ onNavigate }) {
    const [showInterface, setShowInterface] = useState(false);

  const onStartClick = () => {
    setShowInterface(true);
  };

  if (showInterface) {
    // Remplacez 'NextInterface' par la prochaine interface si elle existe.
    return <Interface5/>;
  }
  return (
    <div style={{ padding: '20px' }}>
      <NavBar progress={60} debit={20} />

      <div style={{ textAlign: 'center' }}>
        <h2>À quelle fréquence votre famille diffuse-t-elle de l'audio?</h2>
        <p>(Spotify, Pandora, Amazon Echo or Alexa, Google Home, etc.)</p>
        <button className="survey-btn" onClick={() => onStartClick()}>Rarement</button>
        <button className="survey-btn" onClick={() => onStartClick()}>Hebdomadairement</button>
        <button className="survey-btn" onClick={() => onStartClick()}>Tous les jours</button>
      </div>
    </div>
  );

  

function NavBar({ progress, debit }) {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
      <img src={logo} alt="Topnet Logo" style={{ height: '40px' }} />
      <ProgressBar progress={progress} />
      <div style={{ fontSize: '20px' }}>{debit} Mbps</div>
    </div>
  );
}

function ProgressBar({ progress }) {
  return (
    <div style={{ width: '50%', backgroundColor: '#444', borderRadius: '5px' }}>
      <div style={{ width: `${progress}%`, backgroundColor: '#ff4081', height: '10px', borderRadius: '5px' }}></div>
    </div>
  );
}
}

export default Interface4;
