import React, { useState } from 'react';
import logo from '../assets/logo.png';
import logo1 from '../assets/img.PNG';
import Interface2 from './Interface2';

function Interface1({ onNavigate }) {
   /* const [showInterface2, setShowInterface2] = useState(false);
   

    const handleOptionClick = (increase) => {

        setShowInterface2(true); // Afficher Interface2 après le clic
    };

    if (showInterface2) {
        return <Interface2 onNavigate={onNavigate}/>;
    }
*/
    return (
        <div className="survey-content">
            <NavBar />
            <h1>Combien d'appareils dans votre maison se connectent à Internet?</h1>
            <p>(Ordinateurs, smartphones, tablettes, consoles de jeu, téléviseurs intelligents, appareils domestiques intelligents, etc.)</p>
            <div><img src={logo1} alt="" /></div>
            <div className="survey-options">
                <button className="survey-btn" onClick={() => handleOptionClick(5)}>1-3</button>
                <button className="survey-btn" onClick={() => handleOptionClick(10)}>4-7</button>
                <button className="survey-btn" onClick={() => handleOptionClick(15)}>8-10</button>
                <button className="survey-btn" onClick={() => handleOptionClick(20)}>10+</button>
            </div>
        </div>
    );
}

function NavBar() {
    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#333', color: '#fff', padding: '10px' }}>
            <img src={logo} alt="" style={{ height: '40px' }} />
      <div style={{ fontSize: '20px' }}></div>
        </div>
    );
}


  

export default Interface1;
