import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles.css';  // Assurez-vous d'importer les styles
import NavBar from '../NavBar';
import { EmployeeContext } from '../EmployeeContext';  // Importe le contexte

const Ife2 = () => {
    const navigate = useNavigate();
    const { employeeCount } = useContext(EmployeeContext);  // Récupère la valeur globale de employeeCount
    const [localEmployeeCount, setLocalEmployeeCount] = useState(0);  // Initialise un compteur local à 0
    const [qualityCounts, setQualityCounts] = useState({
        "Vidéo floue": 0,
        "Basse définition": 0,
        "Définition standard": 0,
        "HD (1080p)": 0,
        "Ultra HD (4k)": 0,
        "Je ne sais pas": 0,
    });  // Compteurs pour chaque qualité vidéo
    const [displayCounters, setDisplayCounters] = useState({}); // État pour savoir quels compteurs sont affichés

    const goToInterface3 = () => {
        navigate('/Entreprise/Ifee3');  // Navigue vers l'interface 3
    };

    const handleBackClick = () => {
        navigate('/Entreprise/Ife');  // Retourne à l'interface précédente
    };

    const incrementCount = () => {
        if (localEmployeeCount < employeeCount) {
            setLocalEmployeeCount(localEmployeeCount + 1);  // Modifie uniquement la valeur locale
        }
    };

    const decrementCount = () => {
        if (localEmployeeCount > 0) {
            setLocalEmployeeCount(localEmployeeCount - 1);  // Modifie uniquement la valeur locale
        }
    };

    // Incrémente le compteur pour la qualité vidéo sélectionnée
    const handleQualityIncrement = (quality) => {
        setQualityCounts(prevCounts => ({
            ...prevCounts,
            [quality]: prevCounts[quality] + 1,
        }));
    };

    // Décrémente le compteur pour la qualité vidéo sélectionnée
    const handleQualityDecrement = (quality) => {
        setQualityCounts(prevCounts => ({
            ...prevCounts,
            [quality]: prevCounts[quality] > 0 ? prevCounts[quality] - 1 : 0,
        }));
    };

    // Gère l'affichage du compteur après un clic
    const handleQualityClick = (quality) => {
        setDisplayCounters(prevState => ({
            ...prevState,
            [quality]: true  // Affiche le compteur pour la qualité cliquée
        }));
    };

    return (
        <div className="main-content">
            <div className="header">
                <NavBar />
            </div>

            <div className="info-tooltip-section">
                <div className="go-back-text" onClick={handleBackClick}>
                    ❮ Retour
                </div>
            </div>

            <div className="survey-content">
                <h3>Pensez à votre moment le plus occupé pour l'utilisation d'Internet. Combien d'employés diffusent la vidéo en même temps? (YouTube, Pluralsight, Lynda.com, etc.)</h3>
                <p className="survey-description">Sur {employeeCount}</p>  {/* Affiche la valeur du contexte sans l'utiliser pour le compteur local */}
                
                <div className="employee-counter">
                    <button className="counter-btn" onClick={decrementCount}>-</button>
                    <span className="employee-count">{localEmployeeCount}</span>  {/* Affiche la valeur locale */}
                    <button className="counter-btn" onClick={incrementCount}>+</button>
                </div>
                
                <button className="next-btn" onClick={goToInterface3}>Suivant</button>
            </div>

            {/* Deuxième partie : Section qualité vidéo */}
            <div className="video-quality-section">
                <p className="quality-description">
                    Facultatif: sélectionnez la qualité de la vidéo que vous diffuserez en continu. La plupart des services comme YouTube et Pluralsight diffusent en HD.
                </p>
                <div className="quality-options">
                    {Object.keys(qualityCounts).map((quality) => (
                        <div key={quality} className="quality-option">
                            <button className="quality-btn" onClick={() => handleQualityClick(quality)}>
                                {quality}
                            </button>

                            {/* Affiche le compteur seulement si le bouton a été cliqué */}
                            {displayCounters[quality] && (
                                <div className="quality-counter">
                                    <button 
                                        className="counter-butn"
                                        onClick={() => handleQualityDecrement(quality)}
                                    >
                                        -
                                    </button>
                                    <span className="quality-count">
                                        {qualityCounts[quality]} devices
                                    </span>
                                    <button 
                                        className="counter-butn"
                                        onClick={() => handleQualityIncrement(quality)}
                                    >
                                        +
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Ife2;
